mapper.insert:
insert into LOCATIONS (LOCATION_ID, STREET_ADDRESS, POSTAL_CODE,
CITY, STATE_PROVINCE, COUNTRY_ID
)
values (#{locationId,jdbcType=NUMERIC}, #{streetAddress,jdbcType=VARCHAR}, #{postalCode,jdbcType=VARCHAR},
#{city,jdbcType=VARCHAR}, #{stateProvince,jdbcType=VARCHAR}, #{countryId,jdbcType=CHAR}
)

mapper.select:
select LOCATION_ID, STREET_ADDRESS, POSTAL_CODE, CITY, STATE_PROVINCE, COUNTRY_ID
from LOCATIONS
where LOCATION_ID = #{locationId,jdbcType=NUMERIC}
